const { stripIndents, oneLine } = require('common-tags');
const Command = require('../base');
const { disambiguation } = require('../../util');

module.exports = class HelpCommand extends Command {
	constructor(client) {
		super(client, {
			name: 'help',
			group: 'util',
			memberName: 'help',
			aliases: ['commands'],
			description: 'Displays a list of available commands, or detailed information for a specified command.',
			details: oneLine`
				The command may be part of a command name or a whole command name.
				If it isn't specified, all available commands will be listed.
			`,
			examples: ['help', 'help prefix'],
			guarded: true,

			args: [
				{
					key: 'command',
					prompt: 'Which command would you like to view the help for?',
					type: 'string',
					default: ''
				}
			]
		});
	}

	async run(msg, args) { // eslint-disable-line complexity
		if(msg.guild != null)
		{
			if(!msg.channel.permissionsFor(msg.guild.member(msg.client.user.id)).has("SEND_MESSAGES"))	
			{
				return;
			}

		}

		const groups = this.client.registry.groups;
		const commands = this.client.registry.findCommands(args.command, false, msg);
		const showAll = args.command && args.command.toLowerCase() === 'all';
		if(args.command && !showAll) {
			if(commands.length === 1) {
				let help = stripIndents`
					${oneLine`
						__Command **${commands[0].name}**:__ ${commands[0].description}
						${commands[0].guildOnly ? ' (Usable only in servers)' : ''}
						${commands[0].nsfw ? ' (NSFW)' : ''}
					`}

					**Format:** ${msg.anyUsage(`${commands[0].name}${commands[0].format ? ` ${commands[0].format}` : ''}`)}
				`;
				if(commands[0].aliases.length > 0) help += `\n**Aliases:** ${commands[0].aliases.join(', ')}`;
				help += `\n${oneLine`
					**Group:** ${commands[0].group.name}
					(\`${commands[0].groupID}:${commands[0].memberName}\`)
				`}`;
				if(commands[0].details) help += `\n**Details:** ${commands[0].details}`;
				if(commands[0].examples) help += `\n**Examples:**\n${commands[0].examples.join('\n')}`;

				msg.author.send(help).then(() => 
				{
					if(msg.channel.type !== 'dm') msg.channel.send('<@' + msg.author.id + '> Sent you a DM with information.').catch(error => console.log("Send Error DM Sent Info - " + error));
				}).catch(error => {console.log("Send Error All - " + error); 	msg.channel.send('<@' + msg.author.id + '> Unable to send you the help DM. You probably have DMs disabled.').catch(error => console.log("Send Error DMs Disabled - " + error));});
				
			} else if(commands.length > 15) {
				return msg.channel.send('<@' + msg.author.id + '> Multiple commands found. Please be more specific.').catch(error => console.log("Send Error Multiple - " + error));
			} else if(commands.length > 1) {
				return msg.channel.send(`<@` + msg.author.id + `> ` + disambiguation(commands, 'commands')).catch(error => console.log("Send Error Multiple 2 - " + error));
			} else {
				return msg.channel.send(
					`<@` + msg.author.id + `> Unable to identify command. Use ${msg.usage(
						null, msg.channel.type === 'dm' ? null : undefined, msg.channel.type === 'dm' ? null : undefined
					)} to view the list of all commands.`
				).catch(error => console.log("Send Error Unidentified - " + error));
			}
		} else if(!showAll) {		
			if(msg.channel.type !== 'dm')
			{
				msg.channel.send("View the entire command list on Slav Bot's site.\n\nTo enable Slav Bot's respond system, use `" + msg.guild.commandPrefix + "respond enable`.\nTo get the entire command list via a DM, use `" + msg.guild.commandPrefix + "help all` (If DMs are disabled, then the list will be sent to the channel).\n\nhttps://slavbot.com/")
				.then(() => {
					msg.channel.send("For various settings such as custom responses, visit the settings page on our site: https://slavbot.com/settings").then(() => {
						msg.channel.send(
							"For any further help, visit our support server\n\n" + msg.client.options.invite
						).catch(error => console.log("Send Error Support Link Channel - " + error));	
					}).catch(error => 
					{
						console.log("Send Error Channel Settings - " + error);
					});
				}).catch(error => 
				{
					console.log("Send Error Channel - " + error);
				});
			}
			else
			{
				msg.author.send("View the entire command list on Slav Bot's site.\n\nTo get the entire command list, use `help all`.\n\nhttps://slavbot.com/")
				.then(() => {
					msg.author.send("For various settings such as custom responses, visit the settings page on our site: https://slavbot.com/settings").then(() => {
						msg.author.send(
							"For any further help, visit our support server\n\n" + msg.client.options.invite
						).catch(error => console.log("Send Error Support Link DM - " + error));	
					}).catch(error => console.log("Send Error DM Settings Link - " + error));
				}).catch(error => console.log("Send Error DM Link - " + error));
			}
		}
		else
		{
			msg.author.send(stripIndents`
				${oneLine`
					To run a command in ${msg.guild ? msg.guild.name : 'any server'},
					use ${Command.usage('command', msg.guild ? msg.guild.commandPrefix : null, this.client.user)}.
					For example, ${Command.usage('prefix', msg.guild ? msg.guild.commandPrefix : null, this.client.user)}.
				`}
				To run a command in this DM, simply use ${Command.usage('command', null, null)} with no prefix.
				
				Use ${this.usage('<command>', null, null)} to view detailed information about a specific command.

				Slav Bot also has a response system that can be enabled using the \`respond\` command.
				For Example, \`respond enable\` to enable the respond system and \`respond disable\` to disable it.

				__**${showAll ? 'All commands' : `Available commands in ${msg.guild || 'this DM'}`}**__

				${(showAll ? groups : groups.filter(grp => grp.commands.some(cmd => cmd.isUsable(msg))))
					.map(grp => stripIndents`
						__${grp.name}__
						${(showAll ? grp.commands : grp.commands.filter(cmd => cmd.isUsable(msg)))
							.map(cmd => `**${cmd.name}:** ${cmd.description}${cmd.nsfw ? ' (NSFW)' : ''}`).join('\n')
						}
					`).join('\n\n')
				}
			`, { split: true }).then(() => {
				msg.author.send("For various settings such as custom responses, visit the settings page on our site: https://slavbot.com/settings").catch(error => console.log("Send Error DM Sent - " + error));
				if(msg.channel.type !== 'dm') msg.channel.send('<@' + msg.author.id + '> Sent you a DM with information.').catch(error => console.log("Send Error DM Sent - " + error));
			}).catch(error => 
				{
					if(msg.channel.type === 'dm') return;
					console.log("Send Error DM - " + error);
					msg.channel.send(stripIndents`
					${oneLine`
						To run a command in ${msg.guild ? msg.guild.name : 'any server'},
						use ${Command.usage('command', msg.guild ? msg.guild.commandPrefix : null, this.client.user)}.
						For example, ${Command.usage('prefix', msg.guild ? msg.guild.commandPrefix : null, this.client.user)}.
					`}
					To run a command in this DM, simply use ${Command.usage('command', null, null)} with no prefix.

					Use ${this.usage('<command>', null, null)} to view detailed information about a specific command.

					Slav Bot also has a response system that can be enabled using the \`respond\` command.
					For Example, \`respond enable\` to enable the respond system and \`respond disable\` to disable it.

					__**${showAll ? 'All commands' : `Available commands in ${msg.guild || 'this DM'}`}**__

					${(showAll ? groups : groups.filter(grp => grp.commands.some(cmd => cmd.isUsable(msg))))
						.map(grp => stripIndents`
							__${grp.name}__
							${(showAll ? grp.commands : grp.commands.filter(cmd => cmd.isUsable(msg)))
								.map(cmd => `**${cmd.name}:** ${cmd.description}${cmd.nsfw ? ' (NSFW)' : ''}`).join('\n')
							}
						`).join('\n\n')
					}
				`, { split: true }).then(() => {
					msg.author.send("For various settings such as custom responses, visit the settings page on our site: https://slavbot.com/settings").catch(error => console.log("Send Error DM Sent - " + error));
				}).catch(error => console.log("Send Error Channel Fail - " + error)); 
				});
		}
	}
};
